#include <iostream>#include <cstdio>#include <algorithm>#include <cstring>#include <vector>#include <list>#include <string>#include <map>#include <set>#include <deque>#include <sstream>#include <utility>#include <functional>#include <numeric>#include <stack>#include <queue>#include <climits>#include <cmath>#include <cstdlib>#include <iomanip>#include <ctime>#include <memory.h>#include <cassert>using namespace std;int main() {  int n, k;  cin >> n >> k;  int time_left = 240 - k;  int can_solve = 0;  int iteration = 1;  while((iteration <= n) && (time_left - (5 * iteration) >= 0)) {    can_solve++;    time_left -= (5 * iteration);    iteration++;  }  cout << can_solve << endl;  return 0;}